mapper.select:
        SELECT


--         级联表的字段

        FROM fangjian  a



                and a.id in

                #{item}



                and a.fangjian_name like CONCAT('%',#{params.fangjianName},'%')


                and a.fangjian_types = #{params.fangjianTypes}


                  and a.fangjian_number >= #{params.fangjianNumberStart}   


                  and a.fangjian_number <= #{params.fangjianNumberEnd}   


                and a.fangjian_number = #{params.fangjianNumber}




        order by a.${params.orderBy} desc
